import React, { useState, useEffect } from "react";
import "./styles.scss";
import { AiOutlineUser } from "react-icons/ai";
import { Button } from "react-bootstrap";
import { useHistory } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import fire from "../../../constants/firebase";

const FormLogin = () => {
  //-------------------------------//
  const [user, setUser] = useState("");
  const [email, setEmail] = useState("");
  const [emailError, setEmailError] = useState("");
  const [password, setPassword] = useState("");
  const [passwordError, setPasswordError] = useState(false);
  const [hasAccount, setHasAccount] = useState(false);

  const history = useHistory();

  const clearInputs = () => {
    setEmail("");
    setPassword("");
  };

  const clearErrors = () => {
    setEmailError("");
    setPasswordError("");
  };

  const handleInputChangeUser = (e) => {
    setInputValue(e.target.value);
  };
  const handleInputChangeEmail = (e) => {
    setInputValue(e.target.value);
  };

  const handleLogin = (event) => {
    console.log("Entro en el login ");
    event.preventDefault();
    clearErrors();
    fire
      .auth()
      .signInWithEmailAndPassword(email, password)
      .then(() => {
        console.log("redirigiendo");
        history.push("/administrativo");
      })
      .catch((err) => {
        switch (err.code) {
          case "auth/invalid-email":
          case "auth/user-disable":
          case "auth/user-not.found":
            setEmailError(err.message);
            break;
          case "auth/wrong-password":
            setPasswordError(err.message);
            break;
        }
      });
  };

  const handleSignup = () => {
    clearErrors();
    fire
      .auth()
      .createUserWithEmailAndPassword(email, password)
      .catch((err) => {
        switch (err.code) {
          case "auth/email-already-in-use":
          case "auth/invalid-email":
            setEmailError(err.message);
            break;
          case "auth/weak-password":
            setPasswordError(err.message);
            break;
        }
      });
  };

  const handleLogout = () => {
    console.log("desloguearse");
    fire.auth().signOut();
    history.push("/");
  };

  const authListener = () => {
    fire.auth().onAuthStateChanged((user) => {
      if (user) {
        clearInputs();
        setUser(user);
      } else {
        setUser("");
      }
    });
  };

  const useEffect =
    (() => {
      authListener();
    },
    []);
  return (
    <div className="form">
      <form className="form__container" onSubmit={handleLogin}>
        <h2 className="form__container--title">Inicio de Sesión</h2>
        <div className="form__container--input-container form-group">
          <input
            className="form__container--input-container--input"
            value={user}
            onchange={handleInputChangeUser}
          />
          <input
            className="form__container--input-container--input"
            value={email}
            onchange={handleInputChangeEmail}
          />
        </div>
        <p className="errorMsg"> {passwordError}</p>
        <Button
          type="submit"
          className="btn form__container--button btn-primary"
        >
          Iniciar sesión
        </Button>
      </form>
    </div>
  );
};

export default FormLogin;
