import React, { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import fire from "../../../constants/firebase";
import Input from "./inputs ";
import "./styles.scss";

const FormLogin = () => {
  const [user, setUser] = useState("");
  const [email, setEmail] = useState("");
  const [emailError, setEmailError] = useState("");
  const [password, setPassword] = useState("");
  const [passwordError, setPasswordError] = useState(false);
  const [hasAccount, setHasAccount] = useState(false);

  const history = useHistory();

  function handleChange(name, value) {
    if (name == "usuario") {
      setEmail(value);
    } else {
      if (value.Length < 6) {
        setPasswordError(true);
      } else {
        setPasswordError(false);
        setPassword(value);
      }
    }
  }

  const handleLogin = (event) => {
    console.log("Entro en el login ");
    event.preventDefault();
    fire
      .auth()
      .signInWithEmailAndPassword(email, password)
      .then(() => {
        console.log("redirigiendo");
        history.push("/home");
      })
      .catch((err) => {
        console.log("Entro al error ");
        switch (err.code) {
          case "auth/invalid-email":
          case "auth/user-disable":
          case "auth/user-not.found":
            break;
          case "auth/wrong-password":
            break;
        }
      });
  };

  return (
    <div className="form">
      <form className="form__container" onSubmit={handleLogin}>
        <h2 className="form__container--title">Inicio de Sesi칩n</h2>
        <div className="form__container--input-container form-group">
          <Input
            className="input-usuario"
            attribute={{
              id: "usuario",
              name: "usuario",
              type: "text",
              placeholder: "Usuario",
            }}
            handlechage={handleChange}
          />
          <Input
            className="input-contrasena"
            attribute={{
              id: "contrasena",
              name: "contrasena",
              type: "password",
              placeholder: "contrase침a",
            }}
            handlechage={handleChange}
            param={passwordError}
          />
        </div>
        <button
          type="submit"
          className="btn form__container--button btn-primary"
        >
          Iniciar sesi칩n
        </button>
      </form>
    </div>
  );
};

export default FormLogin;
