import React, { useState } from "react";
import "./styles.scss";
import { AiOutlineUser } from "react-icons/ai";
import { Button } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import fire from '../../../constants/firebase'

const FormLogin = () => {
  const [user, setUser] = useState("");
  const [email, setEmail] = useState("");
  const [emailError, setEmailError] = useState("");
  const [password, setPassword] = useState("");
  const [passwordError, setPasswordError] = useState(false);
  const [hasAccount, setHasAccount] = useState(false);

  const handleLogin = (event) => {
    console.log("Entro en el login ");
    event.preventDefault();
    clearErrors();
    fire
      .auth()
      .signInWithEmailAndPassword(email, password)
      .then(() => {
        console.log("redirigiendo");
        history.push("/administrativo");
      })
      .catch((err) => {
        switch (err.code) {
          case "auth/invalid-email":
          case "auth/user-disable":
          case "auth/user-not.found":
            setEmailError(err.message);
            break;
          case "auth/wrong-password":
            setPasswordError(err.message);
            break;
        }
      });
  };

  return (
    <div className="form">
      <section className="form__container">
        <h2 className="form__container--title">Inicio de Sesión</h2>
        <div className="form__container--input-container">
          <input
            className="form__container--input-container--input"
            type="text"
            placeholder="Usuario"
            value=""
          />
          <input
            className="form__container--input-container--input"
            type="password"
            placeholder="Contraseña"
            value=""
          />
        </div>
        <Button className="btn form__container--button btn-primary">
          Iniciar sesión
        </Button>
      </section>
    </div>
  );
};

export default FormLogin;
