import React, { useState, useEffect } from "react";
import "./styles.scss";
import { AiOutlineUser } from "react-icons/ai";
import { useHistory } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import fire from "../../../constants/firebase";
import Input from './inputs'

const FormLogin = () => {
  //-------------------------------//
  //-------------------------------//
  const [user, setUser] = useState("");
  const [email, setEmail] = useState("");
  const [emailError, setEmailError] = useState("");
  const [password, setPassword] = useState("");
  const [passwordError, setPasswordError] = useState(false);
  const [hasAccount, setHasAccount] = useState(false);

  const handleInputChangeUser = (e) => {
    setUser(e.target.value);
  };

  const handleInputChangeEmail = (e) => {
    setEmail(e.target.value);
  };

  const history = useHistory();

  const clearInputs = () => {
    setEmail("");
    setPassword("");
  };

  const clearErrors = () => {
    setEmailError("");
    setPasswordError("");
  };

  function handleChange(name, value) {
    if (name == "usuario") {
      setEmail(value);
    } else {
      if (value.Length < 6) {
        setPasswordError(true);
      } else {
        setPasswordError(false);
        setPassword(value);
      }
    }
  }

  const handleLogin = (event) => {
    console.log("Entro en el login ");
    event.preventDefault();
    fire
      .auth()
      .signInWithEmailAndPassword(email, password)
      .then(() => {
        console.log("redirigiendo");
        history.push("/home");
      })
      .catch((err) => {
        console.log("error logueo");
        switch (err.code) {
          case "auth/invalid-email":
          case "auth/user-disable":
          case "auth/user-not.found":
            setEmailError(err.message);
            break;
          case "auth/wrong-password":
            setPasswordError(err.message);
            break;
        }
      });
  };

  const authListener = () => {
    fire.auth().onAuthStateChanged(({ user }) => {
      if ({ user }) {
        clearInputs();
        setUser({ user });
      } else {
        setUser("");
      }
    });
  };

  const useEffect =
    (() => {
      authListener();
    },
    []);

  return (
    <div className="form">
      <form className="form__container" onSubmit={handleLogin}>
        <h2 className="form__container--title">Inicio de Sesión</h2>
        <div className="form__container--input-container form-group">
          <Input
            className="input-usuario"
            attribute={{
              id: "usuario",
              name: "usuario",
              type: "text",
              placeholder: "Usuario",
            }}
            handlechage={handleChange}
          />
          <Input
            className="input-usuario"
            attribute={{
              id: "usuario",
              name: "usuario",
              type: "text",
              placeholder: "Usuario",
            }}
            handlechage={handleChange}
          />
        </div>
        <button
          type="submit"
          className="btn form__container--button btn-primary"
        >
          Iniciar sesión
        </button>
      </form>
    </div>
  );
};

export default FormLogin;
