import React, { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import fire from "../../../constants/firebase";
import Input from "./inputs ";
import "./styles.scss";

const FormLogin = () => {
  const [user, setUser] = useState("");
  const [email, setEmail] = useState("");
  const [emailError, setEmailError] = useState("");
  const [password, setPassword] = useState("");
  const [passwordError, setPasswordError] = useState(false);
  const [hasAccount, setHasAccount] = useState(false);

  const history = useHistory();

  function handleChange(name, value) {
    if (name == "usuario") {
      setEmail(value);
    } else {
      if (value.Length < 6) {
        setPasswordError(true);
      } else {
        setPasswordError(false);
        setPassword(value);
      }
    }
  }

  const handleLogin = (event) => {
    console.log("Entro en el login ");
    event.preventDefault();
    clearErrors();
    fire
      .auth()
      .signInWithEmailAndPassword(email, password)
      .then(() => {
        console.log("redirigiendo");
        history.push("/home");
      })
      .catch((err) => {
        console.log("Entro al error ");
        switch (err.code) {
          case "auth/invalid-email":
          case "auth/user-disable":
          case "auth/user-not.found":
            setEmailError(err.message);
            break;
          case "auth/wrong-password":
            setPasswordError(err.message);
            break;
        }
      });
  };

  const handleSignup = () => {
    clearErrors();
    fire
      .auth()
      .createUserWithEmailAndPassword(email, password)
      .catch((err) => {
        switch (err.code) {
          case "auth/email-already-in-use":
          case "auth/invalid-email":
            setEmailError(err.message);
            break;
          case "auth/weak-password":
            setPasswordError(err.message);
            break;
        }
      });
  };

  const handleLogout = () => {
    console.log("desloguearse");
    fire.auth().signOut();
    history.push("/");
  };

  const authListener = () => {
    fire.auth().onAuthStateChanged((user) => {
      if (user) {
        clearInputs();
        setUser(user);
      } else {
        setUser("");
      }
    });
  };

  const useEffect =
    (() => {
      authListener();
    },
    []);

  return (
    <div className="form">
      <form className="form__container" onSubmit={handleLogin}>
        <h2 className="form__container--title">Inicio de SesiÃ³n</h2>
        <div className="form__container--input-container form-group">
          <Input
            className="input-usuario"
            attribute={{
              id: "usuario",
              name: "usuario",
              type: "text",
              placeholder: "Usuario",
            }}
            handlechage={handleChange}
          />
          <Input
            className="input-contrasena"
            attribute={{
              id: "contrasena",
              name: "contrasena",
              type: "password",
              placeholder: "contraseÃ±a",
            }}
            handlechage={handleChange}
            param={passwordError}
          />
        </div>
        <button
          type="submit"
          className="btn form__container--button btn-primary"
        >
          Iniciar sesiÃ³n
        </button>
      </form>
    </div>
  );
};

export default FormLogin;
