import React, { useState } from "react";
import Logo from "../../../assets/images/logo.png";
import { InputGroup,FormControl } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import "./styles.scss";

const FormLogin = ({ CambiarEstado }) => {
  const [user, setUser] = useState("");
  const [password, setPassword] = useState("");

  return (
    <div className="form">
      <img src={Logo} className="form__logo" />
      <form className="form__container">
        <h2 className="form__container--title">Inicio de Sesión</h2>
        <div className="form__container--input-container form-group">
        <InputGroup>
          <InputGroup.Prepend>
            <InputGroup.Text id="basic-addon1">@</InputGroup.Text>
          </InputGroup.Prepend>
          <FormControl
            className="form__container--input-container--input"
            placeholder="Usuario"
            type="text"
            value={user}
            onChange={(e) => {
              console.log({ user });
              setUser(e.target.value);
            }}
          />
           </InputGroup>
          <input
            className="form__container--input-container--input"
            placeholder="Contraseña"
            type="password"
            value={password}
            onChange={(e) => {
              console.log({ password });
              setPassword(e.target.value);
            }}
          />
        </div>
        <button
          onClick={CambiarEstado}
          className="btn form__container--button btn-primary"
        >
          Iniciar sesión
        </button>
      </form>
    </div>
  );
};

export default FormLogin;
