import React, { useState,useEffect } from "react";
import "./styles.scss";
import { AiOutlineUser } from "react-icons/ai";
import { Button } from "react-bootstrap";
import { useHistory } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import fire from "../../../constants/firebase";

const FormLogin = () => {
  const [user, setUser] = useState("");
  const [email, setEmail] = useState("");
  const [emailError, setEmailError] = useState("");
  const [password, setPassword] = useState("");
  const [passwordError, setPasswordError] = useState(false);
  const [hasAccount, setHasAccount] = useState(false);

  const history = useHistory();

  const clearInputs = () => {
    setEmail("");
    setPassword("");
  };

  const clearErrors = () => {
    setEmailError("");
    setPasswordError("");
  };

  function handleChange(name, value) {
    if (name == "usuario") {
      setEmail(value);
    } else {
      if (value.Length < 6) {
        setPasswordError(true);
      } else {
        setPasswordError(false);
        setPassword(value);
      }
    }
  }

  const handleLogin = (event) => {
    console.log("Entro en el login ");
    event.preventDefault();
    clearErrors();
    fire
      .auth()
      .signInWithEmailAndPassword(email, password)
      .then(() => {
        console.log("redirigiendo");
        history.push("/administrativo");
      })
      .catch((err) => {
        switch (err.code) {
          case "auth/invalid-email":
          case "auth/user-disable":
          case "auth/user-not.found":
            setEmailError(err.message);
            break;
          case "auth/wrong-password":
            setPasswordError(err.message);
            break;
        }
      });
  };

  const authListener = () => {
    fire.auth().onAuthStateChanged((user) => {
      if (user) {
        clearInputs();
        setUser(user);
      } else {
        setUser("");
      }
    });
  };

  const useEffect = ( () => {
    authListener();
  }, [] )

  return (
    <div className="form">
      <section className="form__container">
        <h2 className="form__container--title">Inicio de SesiÃ³n</h2>
        <div className="form__container--input-container">
          <input
            className="form__container--input-container--input"
            attribute={{
              id: "usuario",
              name: "usuario",
              type: "text",
              placeholder: "Usuario",
            }}
            handlechage={handleChange}
          />
          <input
            className="form__container--input-container--input"
            attribute={{
              id: "contrasena",
              name: "contrasena",
              type: "password",
              placeholder: "contraseÃ±a",
            }}
            handlechage={handleChange}
          />
        </div>
        <Button className="btn form__container--button btn-primary">
          Iniciar sesiÃ³n
        </Button>
      </section>
    </div>
  );
};

export default FormLogin;
